import React, { useRef, useState } from 'react';
import WebcamCapture from './WebcamCapture';
import EmotionDisplay from './EmotionDisplay';
import LoadingSpinner from './LoadingSpinner';
import { analyzeFace } from '../utils/faceApiHelper';
import '../styles/components/EmotionDetector.css';

const EmotionDetector = () => {
  const webcamRef = useRef(null);
  const [emotions, setEmotions] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState(null);

  const captureAndAnalyze = async () => {
    if (isProcessing) return;

    try {
      setIsProcessing(true);
      setError(null);

      const imageSrc = webcamRef.current.getScreenshot();
      const result = await analyzeFace(imageSrc);

      if (result.faces && result.faces.length > 0) {
        setEmotions(result.faces[0].attributes.emotion);
      } else {
        setError('No face detected. Please try again.');
      }
    } catch (err) {
      setError('Error processing image. Please try again.');
      console.error(err);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="emotion-detector">
      <WebcamCapture webcamRef={webcamRef} />
      <button 
        className="capture-button"
        onClick={captureAndAnalyze}
        disabled={isProcessing}
      >
        Detect Emotion
      </button>
      {isProcessing && <LoadingSpinner />}
      <EmotionDisplay emotions={emotions} error={error} />
    </div>
  );
};

export default EmotionDetector;
